import React, { Component } from 'react'
import "./DatePickerDialog.css";
import 'react-big-calendar/lib/css/react-big-calendar.css';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';


const localizer = momentLocalizer(moment);
 class DatePickerDialog extends Component {
   constructor(props) {
     super(props)

     this.state = {
        events:[ {
          id: 14,
          title: 'Today',
          start: new Date(new Date().setHours(new Date().getHours() - 3)),
          end: new Date(new Date().setHours(new Date().getHours() + 3)),
      },]
     }
   }

  dismissDialog = (e) => {
    if(!e.target.classList.contains('modal-bg')) {
      return;
    }
    console.log('Closed');
if(this.props.dismissible) {
  this.props.dialogDismissed();
}
  }
  render() {
    return (
      <div className="modal-bg" onClick={this.dismissDialog}>
        <div className='modal-content' style={{ height: '68%'}}>
        <h>Select Available Date for Your Appointments of 60 Units</h>
        <Calendar
            events={this.state.events}
            startAccessor="start"
            endAccessor="end"
            defaultDate={moment().toDate()}
            localizer={localizer}
          />
        </div>
      </div>
    )
  }
}
export default DatePickerDialog;
